import React, { Component } from 'react';
//import logo from './logo.svg';
//import './App.css';

class LoginBtn extends Component {

  constructor(){
    this.loadSDK = this.loadSDK.bind(this);
  }

  componentDidMount() {
    loadSDK(document, 'script', 'loginkit-sdk');
    window.snapKitInit = function () {
        var loginButtonIconId = 'my-login-button-target';
        // Mount Login Button
        snap.loginkit.mountButton(loginButtonIconId, {
          clientId: '1ccc96dc-3248-44da-b594-8a9c9ff5ffa6',
          redirectURI: 'https://www.hayterm.com',
          scopeList: [
            'user.display_name',
            'user.bitmoji.avatar',
          ],
          handleResponseCallback: function() {
            snap.loginkit.fetchUserInfo()
              .then(data => console.log('User info:', data));
          },
        });
      };


  }

  loadSDK(d, s, id) {
    var js, sjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s); js.id = id;
        js.src = "https://sdk.snapkit.com/js/v1/login.js";
        sjs.parentNode.insertBefore(js, sjs);
  }

  render() {
    return (
      <div id="my-login-button-target" />

    );
  }
}


export default LoginBtn;
